import { BASE_COMMENT_URL } from '@/constants/API'

export const LOAD_COMMENTS = 'LOAD_COMMENTS'
export const LOAD_COMMENTS_ASYNC = 'LOAD_COMMENTS_ASYNC'
export const ADD_COMMENT = 'ADD_COMMENT'
export const ADD_COMMENT_ASYNC = 'ADD_COMMENT_ASYNC'
export const UPDATE_COMMENT = 'UPDATE_COMMENT'
export const UPDATE_COMMENT_ASYNC = 'UPDATE_COMMENT_ASYNC'
export const DELETE_COMMENT = 'DELETE_COMMENT'
export const DELETE_COMMENT_ASYNC = 'DELETE_COMMENT_ASYNC'

const comment = {
  state: {
    docs: []
  },
  getters: {
    commentCount: state => state.docs.length,
    getCommentById: state => id => state.docs.find(doc => doc._id === id),
    getCommentsByPostId: state => id => state.docs.filter(doc => doc.post && doc.post._id === id),
    getCommentsByUserId: state => id => state.docs.filter(doc => doc.user && doc.user._id === id),
    getCommentsCountByPostId: (state, getters) => id => getters.getCommentsByPostId(id).length
  },
  actions: {
    /* 获取全部评论 */
    [LOAD_COMMENTS_ASYNC]({ commit }, payload) {
      return new Promise((resolve, reject) => {
        this._vm.$axios({
          commit,
          data: payload,
          url: BASE_COMMENT_URL,
          doHideAlert: true,
          success(result) {
            // 保存类别
            commit(LOAD_COMMENTS, result.docs)
            // 向前端通知操作成功
            resolve(result.docs)
          },
          fail(err) {
            // 向前端通知操作失败
            reject(err)
          }
        })
      })
    },
    /* 增加评论 */
    [ADD_COMMENT_ASYNC]({ commit }, payload) {
      return new Promise((resolve, reject) => {
        this._vm.$axios({
          commit,
          method: 'post',
          headers: { Authorization: sessionStorage.getItem('token') },
          data: payload,
          url: BASE_COMMENT_URL,
          success(result) {
            // 保存类别
            commit(ADD_COMMENT, result.doc)
            // 向前端通知操作成功
            resolve(result.doc)
          },
          fail(err) {
            // 向前端通知操作失败
            reject(err)
          }
        })
      })
    },
    /* 更新评论 */
    [UPDATE_COMMENT_ASYNC]({ commit }, payload) {
      return new Promise((resolve, reject) => {
        this._vm.$axios({
          commit,
          method: 'put',
          headers: { Authorization: sessionStorage.getItem('token') },
          data: payload.data,
          url: `${BASE_COMMENT_URL}/${payload.id}`,
          success(result) {
            // 保存类别
            commit(UPDATE_COMMENT, result.doc)
            // 向前端通知操作成功
            resolve(result.doc)
          },
          fail(err) {
            // 向前端通知操作失败
            reject(err)
          }
        })
      })
    },
    /* 删除评论 */
    [DELETE_COMMENT_ASYNC]({ commit }, payload) {
      return new Promise((resolve, reject) => {
        this._vm.$axios({
          commit,
          method: 'delete',
          headers: { Authorization: sessionStorage.getItem('token') },
          url: `${BASE_COMMENT_URL}/${payload.id}`,
          success(result) {
            // 保存类别
            commit(DELETE_COMMENT, result.doc)
            // 向前端通知操作成功
            resolve(result.doc)
          },
          fail(err) {
            // 向前端通知操作失败
            reject(err)
          }
        })
      })
    }
  },
  mutations: {
    /* 保存所有评论 */
    [LOAD_COMMENTS](state, payload) {
      state.docs = payload
    },
    /* 保存增加的评论到评论数组中 */
    [ADD_COMMENT](state, payload) {
      state.docs = [payload, ...state.docs]
    },
    /* 更新评论 */
    [UPDATE_COMMENT](state, payload) {
      state.docs = state.docs.map(t => {
        if (t._id === payload._id) {
          return payload
        }
        return t
      })
    },
    /* 删除评论 */
    [DELETE_COMMENT](state, payload) {
      state.docs = state.docs.filter(t => t._id !== payload._id)
    }
  }
}
export default comment

import { BASE_POST_URL } from '@/constants/API'
import { getCategoryNumbers, getNumberWithoutPostPositiveZero, getAncestorNumber } from '@/utils/util'

export const LOAD_POSTS = 'LOAD_POSTS'
export const LOAD_POSTS_ASYNC = 'LOAD_POSTS_ASYNC'
export const LOAD_POST = 'LOAD_POST'
export const LOAD_POST_ASYNC = 'LOAD_POST_ASYNC'
export const CLEAR_POST = 'CLEAR_POST'

const post = {
  state: {
    docs: [],
    doc: null
  },
  getters: {
    postCount: state => state.docs.length,
    getPosts: state => state.docs,
    getPost: state => state.doc,
    getPostId: state => {
      if (state.doc) {
        return state.doc._id
      }
    },
    getPostsFilterByCategoryNumber: state => number => {
      if (number === null) {
        return state.docs
      }
      const reg = new RegExp(`^${getNumberWithoutPostPositiveZero(number)}`)
      return state.docs.filter(doc => doc.category && String(doc.category.number).match(reg))
    },
    getPostTitleByPostId: state => id => state.docs.find(doc => doc._id === id).title,
    getPostWithTitleDatas: (state, getters) => {
      const { doc } = state
      if (doc) {
        return {
          ...doc,
          titleDatas: doc.category && Object.keys(getters.getCategoriesByNumber).length !== 0
            ? getCategoryNumbers(doc.category.number).map(t => getters.getCategoriesByNumber[t].name) : [],
          text: typeof window === 'object' ? decodeURIComponent(escape(window.atob(doc.content))) : ''
        }
      }
      return null
    },
    getPostsWithTitleData: (state, getter) => {
      if (getter.getPosts.length === 0) return getter.getPosts
      if (Object.keys(getter.getCategoriesByNumber).length === 0) return getter.getPosts
      return (
        getter.getPosts.map(t => ({
          ...t,
          titleData: t.category ? getter.getCategoriesByNumber[getAncestorNumber(t.category.number)].name : ''
        }))
      )
    },
    getRecommendPosts: (state, getter) => getter.getPostsWithTitleData.filter(t => t.recommend).sort((a, b) => a.index - b.index)
  },
  actions: {
    /* 按照id获取一篇文章 */
    [LOAD_POST_ASYNC]({ commit }, payload) {
      return new Promise((resolve, reject) => {
        this._vm.$axios({
          commit,
          url: `${BASE_POST_URL}/${payload.id}`,
          doHideAlert: true,
          success(result) {
            // 保存文章
            commit(LOAD_POST, result.doc)
            // 向前端通知操作成功
            resolve(result.doc)
          },
          fail(err) {
            // 向前端通知操作失败
            reject(err)
          }
        })
      })
    },
    /* 获取所有文章 */
    [LOAD_POSTS_ASYNC]({ commit }) {
      return new Promise((resolve, reject) => {
        this._vm.$axios({
          commit,
          url: `${BASE_POST_URL}`,
          doHideAlert: true,
          success(result) {
            // 保存文章
            commit(LOAD_POSTS, result.docs)
            // 向前端通知操作成功
            resolve(result.docs)
          },
          fail(err) {
            // 向前端通知操作失败
            reject(err)
          }
        })
      })
    }
  },
  mutations: {
    /* 保存所有文章信息 */
    [LOAD_POSTS](state, payload) {
      state.docs = payload
    },
    /* 保存当前文章信息 */
    [LOAD_POST](state, payload) {
      state.doc = payload
    },
    /* 清空当前文章信息 */
    [CLEAR_POST](state) {
      state.doc = null
    }
  }
}
export default post

